import React, { useState, useEffect } from "react";
import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View } from "react-native";
import MapView, { PROVIDER_GOOGLE, Marker, Callout } from "react-native-maps";
import * as Location from "expo-location";

export default function App() {
    const [currentLoc, setCurrentLoc] = useState(null);
    const [errorMsg, setErrorMsg] = useState(null);

    const [locations, setLocations] = useState(null);

    useEffect(() => {
        (async () => {
            console.log("hey");
            let { status } = await Location.requestForegroundPermissionsAsync();
            if (status !== "granted") {
                setErrorMsg("Permission to access location was denied");
                return;
            }

            await Location.getCurrentPositionAsync({}).then((res) => {
                console.log(res);
                setCurrentLoc(location);
            });

            console.log(currentLoc);

            //console.log(location);
        })();
    }, []);

    useEffect(() => {
        setLocations([
            {
                name: "Recycling station 1",
                latitude: currentLoc.coords.latitude + 0.005,
                longitude: currentLoc.coords.longitude,
            },
            {
                name: "Recycling station 2",
                latitude: currentLoc.coords.latitude,
                longitude: currentLoc.coords.longitude + 0.005,
            },
        ]);
    }, [locations]);

    let text = "Waiting..";
    if (errorMsg) {
        text = errorMsg;
    } else if (currentLoc) {
        text = JSON.stringify(currentLoc);
    }

    return !currentLoc ? (
        <></>
    ) : (
        <MapView
            provider={PROVIDER_GOOGLE}
            style={styles.map}
            initialRegion={{
                latitude: currentLoc.coords.latitude,
                longitude: currentLoc.coords.longitude,
                latitudeDelta: 0.0922,
                longitudeDelta: 0.0421,
            }}
            showsUserLocation={true}
            followsUserLocation={true}
            showsMyLocationButton={true}
        >
            {/* {locations.map((loc) => (
                <Marker
                    coordinate={{
                        latitude: loc.latitude,
                        longitude: loc.longitude,
                    }}
                >
                    <Callout tooltip>
                        <View style={styles.container}>
                            <Text>{loc.name}</Text>
                        </View>
                    </Callout>
                </Marker>
            ))} */}
        </MapView>
    );
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "white",
        justifyContent: "flex-end",
        alignItems: "center",
        padding: 10,
    },
    map: {
        ...StyleSheet.absoluteFillObject,
    },
});
